<!-- Vue Spoiler, http://tpkn.me/ -->
<template>
   <div :class="['vue-spoiler', { 'expanded': expanded }]" :style="{ width }">
      <div @click="toggleExpand" class="title">
         <div v-if="title">{{ getTitle }}</div>
         <slot v-else name="title"></slot>
         <div v-if="arrow" class="arrow"></div>
      </div>
      <transition name="opacity" mode="out-in" appear>
         <div v-if="expanded" class="content" :style="{ 'max-height': height }">
            <slot></slot>
         </div>
      </transition>
   </div>
</template>
<script>

   export default {
      name: 'Spoiler',

      data: () => {
         return {
            expanded: false
         }
      },

      props: {
         'expand': {
            default: () => false
         },
         'width': {
            default: () => '100%'
         },
         'height': {
            default: () => 'none'
         },
         'title': {
            default: () => ''
         },
         'titleExpanded': {
            default: () => ''
         },
         'arrow': {
            default: () => true
         },
      },

      computed: {
         getTitle(){
            let title;
            if(this.expanded && this.titleExpanded){
               title = this.titleExpanded;
            }else{
               title = this.title;
            }
            return title;
         }
      },

      methods: {
         toggleExpand(){
            this.expanded = !this.expanded;
            this.$emit('expanded', this.expanded);
         }
      },

      beforeMount(){
         this.expanded = this.expand;
      },

      watch: {
         expand(){
            this.expanded = this.expand;
         },
      }
   }

</script>
<style lang="scss" scoped>

   .vue-spoiler {
      position: relative;

      .title {
         position: relative;
         display: flex;
         align-items: center;
         
         word-break: break-word;
         cursor: pointer;
         user-select: none;
      }

      .content {
         position: relative;
         overflow-y: auto;
         word-break: break-word;
      }

      .arrow {
         width: 25px;
         height: 25px;

         padding: 10px 0;
         flex-shrink: 0;

         background-image: url('data:image/svg+xml;base64,');
         /* background-image: url('arrow.svg'); */
         /* background-image: url('arrow-close.svg'); */
         background-position: 50% 100%;
         background-repeat: no-repeat;
         background-size: 100%;

         transition: background-position 0.2s cubic-bezier(0.490, 0.265, 0.450, 1);
      }
   }

   .vue-spoiler.expanded .arrow {
      background-position: 50% 0;
   }  
   

   /* Expand animation */
   .opacity-enter-active {
      transition: opacity .2s ease-out;
   }
   .opacity-leave-active {
      transition: 0;
   }
   .opacity-enter, .opacity-leave-to {
      opacity: 0;
   }

</style>
